import React, {useState} from 'react';

const Counter = () => {

  const initValue = 100;
  const [counter, setCounter] = useState(initValue);
  const [number, seNumber] = useState(0);


  const inputValue = (e) => {
    const val = e.target.value;
    console.log(val);  
  };

    return (

      <div className="counter">
        <h2>Licznik</h2>
        <p>Stan licznika - <span className="bold">{counter}</span></p>
        <button onClick={() => setCounter(counter + 1)}>Dodaj 1</button>
        <button onClick={() => setCounter(initValue)}>Reset</button>
        <button onClick={() => setCounter(counter - 1)}>Odejmij 1</button>
        <input type="number" onChange={this.inputValue()} />
      </div>
    )
}

export default Counter;
